import axios from "axios";
import { useState } from "react";
import { useForm } from "react-hook-form";
import "../../styles/layout/layout.css";

function FindPassword() {
	type ResponseList = {
		bindingResultHasErrors: boolean;
		noEmailLikeThat: boolean;
	};
	const [response, setResponse] = useState<ResponseList>({
		bindingResultHasErrors: false,
		noEmailLikeThat: false,
	});
	// submitted==true여야 새로고침 되도록.
	const [submitted, setSubmitted] = useState(false);

	const onSubmit = async (data: any) => {
		await new Promise((r) => setTimeout(r, 100));

		// alert(JSON.stringify(data));
		console.log(data);

		await axios
			.post("/joinForm", data)
			.then((res) => {
				console.log("postHere");
				console.log(data);
				setResponse(res.data);
				console.log(res.data);
				setSubmitted(true);
			})
			.catch(function (error) {
				console.log(error.config);
			});
	};

	function RedirectAndInputErrors() {
		if (response.bindingResultHasErrors && response.noEmailLikeThat) {
			return (
				<>
					<div className="user-text-error">bindingResultHasErrors</div>
					<div className="user-text-error">noEmailLikeThat</div>
				</>
			);
		} else if (response.bindingResultHasErrors) {
			return <div className="user-text-error">bindingResultHasErrors</div>;
		} else if (response.noEmailLikeThat) {
			return <div className="user-text-error">noEmailLikeThat</div>;
		} else if (submitted) {
			// 위 조건 만족할 때만 loginForm으로 새로고침
			window.location.href = "/loginForm";
			return null;
		}
		return null;
	}

	const {
		register,
		handleSubmit,
		formState: { isSubmitting, isDirty, errors },
	} = useForm();

	return (
		<div className="FindPassword-container">
			<div className="container">
				<form>
					<p>입력한 이메일로 임시 비밀번호가 전송됩니다.</p>
					<div className="input-group input-group-outline my-3">
						<label className="form-label">Email</label>
						<input
							type="email"
							id="userEmail"
							name="memberEmail"
							className="form-control"
							required
						/>
					</div>
					<div className="text-center">
						<button
							type="button"
							className="btn bg-gradient-primary w-100 my-4 mb-2"
							id="checkEmail"
						>
							비밀번호 발송
						</button>
					</div>
				</form>
			</div>
		</div>
	);
}

export default FindPassword;
