import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import QuestionNoteListAnony from "../../components/QuestionNote/QuestionNoteListAnony";
import SendQuestionForm from "../../components/SendQuestionForm/SendQuestionFormUserHome";
import "../../styles/layout/layout.css";
import "../../styles/userHome.css";

import { toast, ToastContainer, Zoom } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { IAnswer, IQuestion } from "../../components/types";
import HeaderNoProfile from "../../components/Header/HeaderNoProfile";
import Toastify from "../../components/utils/Toast";

function SuggestionPage() {
	const params = useParams();
	const userName = String(params.userName);
	const [userRingcardName, setUseRingcardrName] = useState<String>();

	const [map, setMap] = useState<[IQuestion, IAnswer][]>();

	const [totalPages, setTotalPages] = useState<Number>(0);
	const [pageNumber, setPageNumber] = useState<Number>(0);
	const questionId = "dummy";
	// const { page } = useParams();

	useEffect(() => {
		axios
			.get("/suggestion")
			.then((res) => {
				console.log(res.data);
				setUseRingcardrName(res.data.userRingcardName);
				setMap(res.data.map);
				setTotalPages(res.data.pageInfo.totalPages);
				setPageNumber(res.data.pageInfo.number + 1);
			})
			.catch((err) => {
				console.log(err);
			});
	}, []);
	return (
		<div className="container">
			<HeaderNoProfile />

			<div className="contents-container block">
				<div className="UserHome-profile-box">
					<img src="/logo/Ringca_logo_background.png" alt="" />
					<div className="UserHome-profile-username">링카 운영진</div>
				</div>
				<div className="UserHome-SendQuestionForm-container">
					<div className="SendQuestionFormUserHome-container">
						<Toastify />
						<form
							className="SendQuestionFormUserHome-question-form"
							onSubmit={handleSubmit(onSubmit)}
						>
							<div className="SendQuestionFormUserHome-questionForm-box">
								<div className="SendQuestionFormUserHome-questionForm-contents-box">
									<span className="SendQuestionFormUserHome-cursur-bar">|</span>
									<textarea
										id="questionAdd"
										className="questionForm-textarea"
										value={checkItemContent}
										onInput={checkItemChangeHandler}
										onKeyUp={checkLengthHandler}
										style={{ height: (textareaHeight + 1) * 20 + "px" }}
										placeholder="질문해 주세요"
									/>
								</div>
							</div>
							<button
								className="SendQuestionFormUserHome-send-question-btn"
								type="submit"
							>
								<img src="/buttons/send-question-btn.svg" alt="" />
							</button>
						</form>
					</div>
				</div>
			</div>
		</div>
	);
}

export default SuggestionPage;
