import axios from "axios";
import React, { useState, useEffect, Component } from "react";
import { useParams } from "react-router-dom";
import EditAnswerFormQuestionNote from "../../components/EditAnswerFormQuestionNote";
import Header from "../../components/Header";
import QuestionNoteList from "../../components/QuestionNoteList";
import "../../styles/layout/layout.css";
import "../../styles/question-page.css";

function QuestionAnswered() {
	const params = useParams();
	const paramsQuestionId = params.questionId;

	const [questionList, setQuestionList] = useState<any[]>([]);
	const [question, setQuestion] = useState<any>([]);
	const [oldAnswer, setOldAnswer] = useState<any>([]);
	// const [oldAnswerTemp, setOldAnswerTemp] = useState<any>();


	useEffect(() => {
		axios
			.get("/question/" + paramsQuestionId + "/edit/user")
			.then((res) => {
				console.log(res.data);
				setQuestionList(res.data.questions);
				setQuestion(res.data.question);
				// setOldAnswerTemp(res.data.oldAnswer);
				// console.log(res.data.oldAnswer);
				// console.log(oldAnswerTemp);
				localStorage.setItem("oldAnswerTemp", res.data.oldAnswer);
				setOldAnswer(localStorage.getItem("oldAnswerTemp"));
				console.log(localStorage.getItem("oldAnswerTemp"));
				console.log(oldAnswer);
				// localStorage.setItem("oldAnswer", res.data.oldAnswer);
				// setOldAnswer(localStorage.getItem("oldAnswer"));
				// console.log(localStorage.getItem("oldAnswer"));
				
				// setOldAnswer(res.data.oldAnswer);
				// localStorage.setItem("oldAnswer", oldAnswer);
				// setOldAnswer(localStorage.getItem("oldAnswer"));
			})
			.catch((err) => {
				console.log(err);
			});
	}, [paramsQuestionId]);

	return (
		<div className="container">
			<Header />
			<div className="UserHome-contents-container">
				<div className="down-background-img">
					<div className="questionPage-the-question-container">
						<EditAnswerFormQuestionNote
							question={question}
							oldAnswer={oldAnswer}
							// oldAnswer={oldAnswerTemp}
						/>
					</div>
					<div className="QuestionPage-hr-box">
						<hr className="QuestionPage-hr" />

						<div className="QuestionPage-hr-text">미응답 질문들</div>
						<hr className="QuestionPage-hr" />
					</div>
					<div className="questionPage-container-body">
						<QuestionNoteList questionList={questionList} />
					</div>
				</div>
			</div>
		</div>
	);
}

export default QuestionAnswered;
