import axios from "axios";
import React, { ReactElement, useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { eventManager } from "react-toastify/dist/core";
import "../../styles/selectNoteModal.css";

interface props {
	open: boolean;
	close: () => void; // 함수 타입 정의할 때
	getNoteColorData: (noteColorName: any) => void;
}

export interface noteColorDataType {
	colorName: string | undefined;
	colorCode: string | undefined;
}

const SelectNoteModal = (props: props): ReactElement => {
	const { open, close, getNoteColorData } = props;
	const [colorName, setColorName] = useState<"연보라">();
	const [colorCode, setColorCode] = useState<"#e9e1ec">();

	const colorDict: any = {};
	colorDict["연보라"] = "#e9e1ec";
	colorDict["복숭아"] = "#f2d4d6";
	colorDict["파랑"] = "#d5d8f3";
	colorDict["노랑"] = "#f3ead5";

	function handleNoteColorClick(event: any): void {
		setColorName(event.target.textContent);
		setColorCode(colorDict[event.target.textContent]);
	}
	useEffect(() => {
		console.log(colorName);
		console.log(colorCode);

		const noteColorData: noteColorDataType = {
			colorName: colorName,
			colorCode: colorCode,
		};
		getNoteColorData(noteColorData);

		const noteColorText = document.getElementById(
			"SelectNoteModal-note-color-text"
		);

		const noteColorCode = document.getElementById(
			"SelectNoteModal-note-color-code"
		);

		if (!!noteColorText) {
			if (noteColorData.colorName != undefined) {
				noteColorText.innerText = noteColorData.colorName;
			} else {
				noteColorText.innerText = "노트색";
			}
		}

		if (!!noteColorCode) {
			if (noteColorData.colorCode != undefined) {
				noteColorCode.style.backgroundColor = noteColorData.colorCode;
			} else {
				noteColorCode.style.backgroundColor = "#e9e1ec";
			}
		}
	}, [colorName, colorCode]);

	return (
		<>
			<div className={open ? "bg" : ""} />
			<div className={open ? "modal active" : "modal"}>
				{open ? (
					<div className="SelectNoteModal-area">
						<div className="SelectNoteModal-header">
							<div className="SelectNoteModal-header-text">질문노트 색상</div>
							<div className="SelectNoteModal-selected-box">
								<div
									className="SendQuestionForm-note-color"
									id="SelectNoteModal-note-color-code"
									style={{ backgroundColor: `${colorCode}` }}
								/>
								<div
									className="SendQuestionForm-note-color-text"
									id="SelectNoteModal-note-color-text"
								>
									{colorName}
								</div>
								<img
									className="SendQuestionForm-open-modal-btn"
									src="/buttons/chevron-note-select-btn.svg"
									alt=""
								/>
							</div>
						</div>

						<div className="SelectNoteModal-hr-box">
							<hr className="SelectNoteModal-hr" />
						</div>
						<div className="SelectNotemodal-colors-box">
							<div
								className="SelectNotemodal-color-option"
								style={{ backgroundColor: "#e9e1ec" }}
								onClick={handleNoteColorClick}
								// onClick={(event) => {
								// 	// alert(event.currentTarget.innerText);
								// 	setColorName(event.currentTarget.innerText);
								// 	// alert(event.currentTarget.innerText);
								// 	alert(colorName);
								// }}
							>
								<div>연보라</div>
							</div>
							<div
								className="SelectNotemodal-color-option"
								style={{ backgroundColor: "#f2d4d6" }}
								onClick={handleNoteColorClick}
							>
								<div>복숭아</div>
							</div>
							<div
								className="SelectNotemodal-color-option"
								style={{ backgroundColor: "#d5d8f3" }}
								onClick={handleNoteColorClick}
							>
								<div>파랑</div>
							</div>
							<div
								className="SelectNotemodal-color-option"
								style={{ backgroundColor: "#f3ead5" }}
								onClick={handleNoteColorClick}
							>
								<div>노랑</div>
							</div>

							{/* 여기다가 계속 색 추가하면 됨 */}
						</div>
					</div>
				) : null}
			</div>
		</>
	);
};
export default SelectNoteModal;
