import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Header from "../../components/Header";
import QuestionNoteList from "../../components/QuestionNoteList";
import { IQuestion } from "../../components/types";

function AnswerCompletedPage() {
	const params = useParams();
	const questionId = params.questionId;

	const [question, setQuestion] = useState<any>();
	const [answer, setAnswer] = useState<any>();
	const [questionList, setQuestionList] = useState<any[]>([]);

	useEffect(() => {
		axios
			.get("/question/" + questionId + "/completed/user")
			.then((res) => {
				console.log(res.data);
				setQuestionList(res.data.questions);
				setQuestion(res.data.question);
				setAnswer(res.data.answer);
			})
			.catch((err) => {
				console.log(err);
			});
	}, [questionId]);

	return (
		<div className="container">
			<Header />
			<div className="contents-container">
				<div className="questionPage-the-question-container">
					<div className="each-question-note-box">
						<div
							className="each-question-note-header-edge-img-box"
							style={{
								backgroundImage: `url("/notes/yellow-note-top-edge.png")`,
							}}
						></div>
						<div className="each-question-note-body">
							<div className="each-note-header">
								<div className="note-profile-pic">
									<img src="/test-anony-profile-pic.jpg" alt="" />
								</div>
								<div className="note-time">5분 전</div>
							</div>
							<div className="each-note-content-box">
								<div className="each-note-content">
									{question.questionContents}
								</div>
							</div>
							<hr className="note-hr" />
							<div className="each-note-answer-form-box">
								{/* <AnswerForm questionId={question.id} /> */}
							</div>
						</div>
						<div
							className="each-question-note-footer-edge-img-box"
							style={{ backgroundImage: `url("/notes/yellow-note-edge.png")` }}
						></div>
					</div>
				</div>
				<div className="questionPage-container-body">
					<QuestionNoteList questionList={questionList} />
				</div>
			</div>
		</div>
	);
}

export default AnswerCompletedPage;
