import axios from "axios";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import QuestionNoteListAnony from "../../components/QuestionNote/QuestionNoteListAnony";
import SendQuestionForm from "../../components/SendQuestionForm/SendQuestionFormUserHome";
import "../../styles/layout/layout.css";
import "../../styles/userHome.css";

import { toast, ToastContainer, Zoom } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import { IAnswer, IQuestion } from "../../components/types";
import HeaderNoProfile from "../../components/Header/HeaderNoProfile";
import Toastify from "../../components/utils/Toast";
import { useForm } from "react-hook-form";

function SuggestionPage() {
	const params = useParams();
	const onSubmit = async (data: any) => {
		await new Promise((r) => setTimeout(r, 100));

		// alert(JSON.stringify(data));
		console.log(data);

		await axios
			.post("/suggestion", data)
			.then((res) => {
				console.log(data);

				localStorage.setItem("toastShow", "1");
				localStorage.setItem("toastText", "질문이 안전하게 전달되었습니다.");
				window.location.reload();
			})
			.catch(function (error) {
				if (error.response) {
					// The request was made and the server responded with a status code
					// that falls out of the range of 2xx
					console.log(error.response.data);
					console.log(error.response.status);
					console.log(error.response.headers);
				} else if (error.request) {
					// The request was made but no response was received
					// `error.request` is an instance of XMLHttpRequest in the browser and an instance of
					// http.ClientRequest in node.js
					console.log(error.request);
				} else {
					// Something happened in setting up the request that triggered an Error
					console.log("Error", error.message);
				}
				console.log(error.config);
			});
	};

	const {
		register,
		handleSubmit,
		setValue,
		// formState: { isSubmitting, isDirty, errors },
	} = useForm();

	useEffect(() => {
		axios
			.get("/suggestion")
			.then((res) => {
				console.log(res.data);
			})
			.catch((err) => {
				console.log(err);
			});
	}, []);
	return (
		<div className="container">
			<HeaderNoProfile />

			<div className="contents-container block">
				<div className="UserHome-profile-box">
					<img src="/logo/Ringca_logo_background.png" alt="" />
					<div className="UserHome-profile-username">링카 운영진</div>
				</div>

				<div className="UserHome-SendQuestionForm-container">
					<div>아 아 보이세요?</div>
					<div className="SendQuestionFormUserHome-container">
						<Toastify />
						<form
							className="SendQuestionFormUserHome-question-form"
							onSubmit={handleSubmit(onSubmit)}
						>
							<div className="SendQuestionFormUserHome-questionForm-box">
								<div className="SendQuestionFormUserHome-questionForm-contents-box">
									<span className="SendQuestionFormUserHome-cursur-bar">|</span>
									<textarea
										id="questionAdd"
										className="questionForm-textarea"
										style={{ height: "300px" }}
										placeholder="질문해 주세요"
									/>
								</div>
							</div>
							<button
								className="SendQuestionFormUserHome-send-question-btn"
								type="submit"
							>
								<img src="/buttons/send-question-btn.svg" alt="" />
							</button>
						</form>
					</div>
				</div>
			</div>
		</div>
	);
}

export default SuggestionPage;
