import { IAnswer, IQuestion } from "./types";
import QuestionFooter from "./QuestionNoteFooter";
import "../styles/question.css";
import NowDate from "./NowDate";
import React, { useState } from "react";

export interface Props {
	idx: number;
	question: IQuestion;
	answerList: IAnswer[];
}

function QuestionNote(props: Props) {
	const idx = props.idx;
	const question = props.question;
	const answerList = props.answerList;
	const [showAnswer, setShowAnswer] = useState<boolean>(false);

	const handleShowAnswerClick = async () => {
		setShowAnswer(!showAnswer);
	};

	function FindAnswer(e: { questionId: number }) {
		if (e.questionId === question.id) {
			return e;
		}
	}

	return (
		<div
			className="question-note"
			key={idx}
			style={{
				backgroundImage: `url("/notes/yellow-note.png")`,
			}}
		>
			<div className="note-header">
				<div className="note-profile-pic">
					<img src="/test-anony-profile-pic.jpg" alt="" />
				</div>
				{/* <div className="note-time">{nowDate}</div> */}
				<NowDate questionUploadTime={question.uploadTime} />
			</div>
			<div className="note-content-box">
				<div className="note-content">{question.questionContents}</div>
				{showAnswer ? <div className="note-content-answer">

				</div> : undefined}
			</div>
			<hr className="note-hr" />
			<div className="QuestionNoteAnony-note-footer">
				<div className="note-footer-rightside-btns-container">
					<button onClick={handleShowAnswerClick}>
						{/* TODO: 얘 이미지 오면 바꾸기 */}
						<img
							className="note-send-answer-btn"
							src="/buttons/send-answer-btn.svg"
							alt=""
						/>
					</button>
				</div>
			</div>
		</div>
	);
}

export default QuestionNote;
