		register,
		handleSubmit,
		formState: { isSubmitting, isDirty, errors },
	} = useForm<FormValues>();

	// PW toggle start.
	const [showPw, setShowPw] = useState<boolean>(false);
	const toggleShowPw = () => {
		setShowPw(!showPw);
	};

	function handlePwClick(e: any) {
		toggleShowPw();
	}

